  return (
    <HistoryContainer>
      <HistoryHeader>
        <span>ðŸ“‹</span> Game History
      </HistoryHeader>

      <HistoryList>
        {gameHistory && gameHistory.length > 0 ? (
          gameHistory.map((round, index) => (
