import React from 'react';
import styled from 'styled-components';
import { useGame } from '../contexts/GameContext';
import Card from './Card';

const HistoryContainer = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  background: linear-gradient(135deg, rgba(10, 34, 25, 0.95) 0%, rgba(0, 0, 0, 0.95) 100%);
  border-left: 2px solid rgba(212, 175, 55, 0.3);
  overflow: hidden;
  box-shadow: -4px 0 20px rgba(0, 0, 0, 0.5);
  backdrop-filter: blur(10px);
`;

const HistoryHeader = styled.div`
  padding: 15px;
  background: linear-gradient(135deg, rgba(10, 34, 25, 0.8) 0%, rgba(0, 0, 0, 0.8) 100%);
  font-weight: 700;
  color: #d4af37;
  border-bottom: 2px solid rgba(212, 175, 55, 0.3);
  display: flex;
  align-items: center;
  text-transform: uppercase;
  letter-spacing: 1px;

  svg {
    margin-right: 8px;
    font-size: 1.2rem;
    color: #d4af37;
  }
`;

const HistoryList = styled.div`
  flex: 1;
  overflow-y: auto;
  background: rgba(10, 34, 25, 0.3);

  /* Scrollbar styling */
  &::-webkit-scrollbar {
    width: 8px;
  }

  &::-webkit-scrollbar-track {
    background: rgba(10, 34, 25, 0.5);
  }

  &::-webkit-scrollbar-thumb {
    background: linear-gradient(135deg, #d4af37, #f4d03f);
    border-radius: 4px;
  }

  &::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(135deg, #f4d03f, #d4af37);
  }
`;

const HistoryItem = styled.div`
  padding: 12px;
  border-bottom: 1px solid rgba(212, 175, 55, 0.2);
  transition: all 0.3s ease;

  &:hover {
    background: rgba(212, 175, 55, 0.1);
    border-left: 3px solid #d4af37;
  }

  &:last-child {
    border-bottom: none;
  }
`;

const RoundHeader = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 10px;
  font-size: 0.85rem;
  color: #aaa;
`;

const RoundNumber = styled.div`
  font-weight: 600;
`;

const TimeStamp = styled.div``;

const ResultGrid = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

const PlayerSection = styled.div`
  padding: 12px;
  background: rgba(10, 34, 25, 0.4);
  border-radius: 8px;
  border: 1px solid rgba(212, 175, 55, 0.2);
`;

const PlayerHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 8px;
`;

const PlayerName = styled.div`
  font-weight: 700;
  color: #d4af37;
  font-size: 1rem;
`;

const PlayerResultBadge = styled.div`
  padding: 4px 12px;
  border-radius: 12px;
