import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';

// Animation: Card flying smoothly from dealer position (top center) to player position
// Simplified for better performance and less stuttering
const dealCard = keyframes`
  from {
    transform: translateY(-50vh) rotate(180deg) scale(0.4);
    opacity: 0;
  }
  to {
    transform: translateY(0) rotate(0deg) scale(1);
    opacity: 1;
  }
`;

// Animation: Card being picked up by dealer (flying back to dealer position)
const pickupCard = keyframes`
  from {
    transform: translate(0, 0) rotate(0deg) scale(1);
    opacity: 1;
  }
  to {
    transform: translate(0, -50vh) rotate(180deg) scale(0.3);
    opacity: 0;
  }
`;

const CardContainer = styled.div`
  position: relative;
  width: 80px;
  height: 120px;
  border-radius: 6px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
  background-color: ${props => props.$hidden ? '#1E1E2E' : 'white'};
  color: ${props => props.$color === 'red' ? '#D32F2F' : '#212121'};
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  padding: 5px;
  margin: 0 -15px;
  transform-origin: center center;
  user-select: none;
  backface-visibility: hidden;
  perspective: 1000px;
  animation: ${props => {
    if (props.$isPickedUp) return `${pickupCard} 0.6s ease-in forwards`;
    if (props.$isNewCard) return `${dealCard} 0.4s ease-out forwards`;
    return 'none';
  }};
  opacity: ${props => {
    if (props.$isPickedUp) return 1;
    if (props.$isNewCard) return 0;
    return 1;
  }};
  ${props => (props.$isNewCard || props.$isPickedUp) ? `
    will-change: transform, opacity;
  ` : ''}
  
  &:hover {
    transform: translateY(-10px);
    z-index: 10;
    transition: transform 0.2s ease;
  }
`;

const CardPattern = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border-radius: 6px;
  background-color: #1E1E2E;
  display: ${props => props.$hidden ? 'block' : 'none'};
  background-image: linear-gradient(45deg, #16213e 25%, transparent 25%),
    linear-gradient(-45deg, #16213e 25%, transparent 25%),
    linear-gradient(45deg, transparent 75%, #16213e 75%),
    linear-gradient(-45deg, transparent 75%, #16213e 75%);
  background-size: 10px 10px;
  background-position: 0 0, 0 5px, 5px -5px, -5px 0px;
`;

const CardBack = styled.div`
  position: absolute;
  top: 10%;
  left: 10%;
  width: 80%;
  height: 80%;
  border-radius: 3px;
  background-color: #101020;
  display: ${props => props.$hidden ? 'block' : 'none'};
  border: 2px solid #e2b714;
`;

const CardTop = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
`;

const CardBottom = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  transform: rotate(180deg);
`;

const CardCenter = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: 2rem;
  font-weight: bold;
`;

const CardValue = styled.div`
  font-size: 1.1rem;
  font-weight: bold;
  line-height: 1;
`;

const CardSuit = styled.div`
  font-size: 1.1rem;
  line-height: 1;
`;

// Helper function to get card suit symbol
const getSuitSymbol = (suit) => {
  switch (suit) {
    case 'hearts':
      return '♥';
    case 'diamonds':
      return '♦';
    case 'clubs':
      return '♣';
    case 'spades':
      return '♠';
    default:
      return '';
  }
};

// Helper function to get card color
const getCardColor = (suit) => {
  return suit === 'hearts' || suit === 'diamonds' ? 'red' : 'black';
};

// Helper function to get card display value
const getCardValue = (value) => {
  switch (value) {
    case 'ace':
      return 'A';
    case 'king':
      return 'K';
    case 'queen':
      return 'Q';
    case 'jack':
      return 'J';
    default:
      return value;
  }
};

const Card = ({ card, hidden = false, isNewCard = false, isPickedUp = false, pickupDelay = 0 }) => {
  const [shouldAnimate, setShouldAnimate] = useState(isNewCard);
  const [shouldPickup, setShouldPickup] = useState(false);
  
  useEffect(() => {
    if (isNewCard) {
      setShouldAnimate(true);
      // Reset animation after it completes
      const timer = setTimeout(() => {
        setShouldAnimate(false);
      }, 400);
      return () => clearTimeout(timer);
    }
  }, [isNewCard]);
  
  useEffect(() => {
    if (isPickedUp && pickupDelay >= 0) {
      const timer = setTimeout(() => {
        setShouldPickup(true);
      }, pickupDelay);
      return () => clearTimeout(timer);
    }
  }, [isPickedUp, pickupDelay]);
  
  if (!card && !hidden) return null;
  
  // If card is hidden, just show the back
  if (hidden) {
    return (
      <CardContainer $hidden={true} $isNewCard={shouldAnimate}>
        <CardPattern $hidden={true} />
        <CardBack $hidden={true} />
      </CardContainer>
    );
  }
  
  const { suit, value } = card;
  const color = getCardColor(suit);
  const suitSymbol = getSuitSymbol(suit);
  const displayValue = getCardValue(value);
  
  return (
    <CardContainer $color={color} $isNewCard={shouldAnimate}>
      <CardTop>
        <CardValue>{displayValue}</CardValue>
        <CardSuit>{suitSymbol}</CardSuit>
      </CardTop>
      
      <CardCenter>
        {suitSymbol}
      </CardCenter>
      
      <CardBottom>
        <CardValue>{displayValue}</CardValue>
        <CardSuit>{suitSymbol}</CardSuit>
      </CardBottom>
    </CardContainer>
  );
};

export default Card;
